if (typeof brightness == "undefined") { brightness = 100 };

var configJSONcore = {
    "name": bplusdevice,
    "location": location,
    "autobackup": autobackup,
    "brightnesslargedisplay": brightness,
    "brightnessminidisplay": brightness,
    "ledcolorfront": 0,
    "ledcolorwall": 0,
    "color": 0,
    "statusbar": statusbar
}

// save the configuration and parameters to this flow, for use
flow.set('bplusdevice', bplusdevice, "file");
flow.set('bplusconfigdevice', bplusconfig[bplusdevice], "file");
flow.set('pages',pages,"file");
flow.set('configJSONcore', configJSONcore, "file");

// save the config of all devices back to the disk
global.set("bplusconfig", bplusconfig, "file")


return null;

